        .moveSpeed(200)
        .loopProperty("alphaFilter", "alpha", { values: [1, 0.6], duration: 500, pingPong: true })
        .duration(2000)
        .waitUntilFinished(index + 1 >= copies.length ? -200 : false)
})

sequ.animation()
